        // TokenDrop Initialize
        tokenDrop.initialize(_measure, _asset, _dripRatePerSecond);

        // Emit LogCreate
        emit LogCreateTokenDrop(address(tokenDrop));

        // Return TokenDrop addresses
        return tokenDrop;
    }
